import React from 'react';
import { login } from '../utils';
import { useState } from 'react';

function SignIn(props) {
    const[user, setUser] = useState();
    const handleLogin = () => {
        login();
        props.history.push('/UserDetails');
    }
   const handleFirstNameChanged=(event) => {
        var customer        = this.state.customer;
        customer.firstName  = event.target.value;

        this.useState({ customer: customer });
      }

    const  handlePassChanged = (event) => {
        var customer      = this.state.customer;
        customer.lastName = event.target.value;

        this.useState({ customer: customer });
      }  
    return (
        <div>
            <h1>Sign in</h1>
            Username: <input type="text" value={this.state.customer.firstName} onChange={this.handleFirstNameChanged.bind(this)}/><br></br>
            Password: <input type="password" value={this.state.customer.lastName} onChange={this.handlePassChanged.bind(this)} /><br></br>
            <button onClick={() => handleLogin()}>Log in</button>
        </div>
    );
};

export default SignIn;
