import React, { Component } from 'react';
import { logout, isLogin } from '../utils';
import { Link } from 'react-router-dom';

class Home extends Component {

    constructor(props) {
        super(props);

        this.state = {
            isLogin: isLogin(),
            customer: {
                firstName: props.firstName,
                lastName: props.lastName,
                status: props.status,
            }
        }
    }
    handleFirstNameChanged(event) {
        var customer        = this.state.customer;
        customer.firstName  = event.target.value;

        this.setState({ customer: customer });
      }

      handlePassChanged(event) {
        var customer      = this.state.customer;
        customer.lastName = event.target.value;

        this.setState({ customer: customer });
      }

      handleStatusChanged(event) {
        var customer    = this.state.customer;
        customer.status = event.target.value;

        this.setState({ customer: customer });
      }

    handleLogout = () => {
        logout();
        this.setState({
            isLogin: false
        })
    }

    render() {
        return (
            <div>
                <h1>Home Page</h1>
                <label>
          First Name: 
        </label>
        <input type="text" value={this.state.customer.firstName} onChange={this.handleFirstNameChanged.bind(this)}/>
        <br/>
        <label>
          Password:
        </label>
        <input type="password" value={this.state.customer.lastName} onChange={this.handlePassChanged.bind(this)}/>
        <br/>
        <label>
          Role:
        </label>
        <select value={this.state.customer.status} onChange={this.handleStatusChanged.bind(this)}>
          <option value="Admin">
            Admin
          </option>
          <option value="User">
            User
          </option>
        </select>
                {this.state.isLogin ? 
                    <button onClick={() => this.handleLogout()}>Click here to log out</button>
                    : <Link to="/signin">Go to sign in page</Link>
                }
            </div>
        );
    }
}

export default Home;
