import React from 'react';
import { login } from '../utils';
import { useState } from 'react';

const SignIn = (props) => {
    const[username, setUser] = useState(null);  
    const[print, setPrint] = useState(false);  
    const[status, setStatus] = useState(null);
    const handleLogin = () => {
        setPrint(true)
        login();
        props.history.push('/UserDetails');
    }
   const handleChange = (e) => {
        setUser(e.target.value)
        setPrint(false)
    }
   const handleStatusChanged = (event) => {    
        setStatus(event.target.value)
        setPrint(false)
      }
    console.log(localStorage.getItem('user'));

    return (
        <div>
            <h1>Sign in</h1>
            Username: <input type="text" value={username} onChange={handleChange} /><br></br>
            Password: <input type="password" /><br></br>
            <select value={status} onChange={handleStatusChanged}>
                <option value="Admin">
                    Admin
                </option>
                <option value="User">
                    User
                </option>
            </select>
            <p>{username}- {status}</p>
            {
                print ?
                localStorage.setItem('user', username) && localStorage.setItem('status', status)
                : null
            }
            <button onClick={() => handleLogin()}>Log in</button>
        </div>
    );
};

export default SignIn;
