import React from 'react';
import { login } from '../utils';
import { useState } from 'react';

const SignIn = (props) => {
    const handleLogin = () => {
        login();
        props.history.push('/UserDetails');
    }
  //  const[user, setUser] = useState("");
   function handleFirstNameChanged(event) {
        var customer        = this.state.customer;
        customer.firstName  = event.target.value;

        this.useState({ customer: customer });
      }

    function  handlePassChanged (event) {
        var customer      = this.state.customer;
        customer.lastName = event.target.value;

        this.useState({ customer: customer });
      }  
    return (
        <div>
            <h1>Sign in</h1>
            Username: <input type="text" value="" onChange={handleFirstNameChanged.bind(this)}/><br></br>
            Password: <input type="password" value="" onChange={handlePassChanged.bind(this)} /><br></br>
            <button onClick={() => handleLogin()}>Log in</button>
        </div>
    );
};

export default SignIn;
