import React from 'react';
import { login } from '../utils';
import { useState } from 'react';

const SignIn = (props) => {
    const[username, setUser] = useState(null);  
    const[print, setPrint] = useState(false);  
    const[status, setStatus] = useState(null);
    if(print) {
        localStorage.setItem('userdata', username);
        localStorage.setItem('status', status);
        console.log(localStorage.getItem('userdata'));
        login();
        props.history.push('/UserDetails');
    }
   const handleChange = (e) => {
        setUser(e.target.value)
        setPrint(false)
        let userArr=[]
        let a = [];
        for(let i=0; i < e.target.value.length; i++)
         a = e.target.value;
        userArr.push(a)
        console.log(userArr)
    }
   const handleStatusChanged = (event) => {    
        setStatus(event.target.value)
        setPrint(false)
      }


    return (
        <div>
            <h1>Sign in</h1>
            Username: <input type="text" value={username} onChange={handleChange} /><br></br>
            Password: <input type="password" /><br></br>
            <select value={status} onChange={handleStatusChanged}>
                <option value="Admin">
                    Admin
                </option>
                <option value="User">
                    User
                </option>
            </select>
            <p>
            {
                print ? <h1>{username}</h1> : null
            }
            </p>
            <button onClick={function handleLogin(){  setPrint(true); }}>Log in</button>
        </div>
    );
};

export default SignIn;
